DROP TABLE IF EXISTS `market`;
/*!40101 SET @saved_cs_client     = @@character_set_client */;
/*!50503 SET character_set_client = utf8mb4 */;
CREATE TABLE `market` (
  `user_id` int unsigned NOT NULL COMMENT 'Идентификатор пользователя',
  `securities_id` int unsigned NOT NULL COMMENT 'Идентификатор ценной бумаги',
  `buy_sell_status_id` int unsigned NOT NULL,
  `amount` int unsigned NOT NULL COMMENT 'Колличество акций',
  `price` decimal(19,2) unsigned DEFAULT NULL COMMENT 'Цена 1 ценной бумаги',
  `created_at` datetime DEFAULT CURRENT_TIMESTAMP COMMENT 'Дата создания строки',
  `updated_at` datetime DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT 'Дата обновления строки',
  PRIMARY KEY (`user_id`,`securities_id`,`buy_sell_status_id`,`amount`),
  KEY `securities_id` (`securities_id`),
  KEY `market_buy_sell_status_id_fk` (`buy_sell_status_id`),
  CONSTRAINT `market_buy_sell_status_id_fk` FOREIGN KEY (`buy_sell_status_id`) REFERENCES `buy_sell_statuses` (`id`) ON DELETE RESTRICT ON UPDATE RESTRICT,
  CONSTRAINT `market_securities_id_fk` FOREIGN KEY (`securities_id`) REFERENCES `securities` (`id`) ON DELETE RESTRICT ON UPDATE RESTRICT,
  CONSTRAINT `market_user_id_fk` FOREIGN KEY (`user_id`) REFERENCES `users` (`id`) ON DELETE RESTRICT ON UPDATE RESTRICT
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_0900_ai_ci COMMENT='Таблица, содержащая информацию о заявках пользователей на покупку или продажу ценных бумаг. \nПринцип такой, что сюда попадают все заявки пользователей на покупку и продажу ценных бумаг с возможностью\nуказания пользователем своей собственной цены. Система должна постоянно сравнивать строки между собой и если \nвстречает строки где 1 и таже акция продается и покупается, высчитывает колличество акции, сумму покупки и продажи,\nпосле чего проводит транзакцию, вычитая у покупателя валюту, а у продавца n-ое колличество акции. В процессе \nесли заявки полностью удовлетворены - строки удаляются. НО так как скорость должна быть очень высокой а обьем данных -\nколлосальным, я не уверен что реализацию таких функций целесообразно делать на стороне БД, если я правильно \nвсе понимаю';
